mapper.insert:
INSERT INTO user_usrgrp (
  user_id,
  usrgrp_id
)
VALUES (
  #{arg0},
  #{arg1}
)

mapper.delete:
DELETE
FROM user_usrgrp
WHERE user_id = #{arg0} AND usrgrp_id = #{arg1}

mapper.select:
SELECT usrgrp_id
FROM user_usrgrp
WHERE user_id = #{arg0}

SELECT user_id
FROM user_usrgrp
WHERE usrgrp_id = #{arg0}
